
public class Datatypes {

	private static final char F = 0;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte age;
		age = 24;
		System.out.println("age = " + age);
		
		short numberOfPeople;
		numberOfPeople = 3000;
		System.out.println("numberOfPeople = " + numberOfPeople);
		
		int DistanceOfMoon;
		DistanceOfMoon = 551451512;
		System.out.println("DistanceOfMoon = " + DistanceOfMoon);
		
		long distanceofstar;
		distanceofstar = 551512550262L;
		System.out.println("distanceofstar = " + distanceofstar);
		
		float salary;
		salary = 57.63f;
		System.out.println("salary = " + salary);
		
		double investment;
		investment = 45151228462.54;
		System.out.println("investment = " + investment);
		
		boolean carfixed;
		carfixed = true;
		System.out.println("carfixed = " + carfixed);
		
		char gender;
		gender = 'f';
		System.out.println("gender = " + gender);
		
		
		
		
		
		
	 

	}

}
